package algorithms.numbers;

public class MathUtils {

	public static String PI_1000 =  "3.";
	
	
	public static String PHI_973 = "1.";
	
	
	public static double logOfBase(int base, double num) {
	    return Math.log(num) / Math.log(base);
	}

	public static double log2e(double num) {
	    return logOfBase(2,num);
	}
	
	public static void main(String[] args) {
		System.out.println(logOfBase(2,32));
	}
}
